import React, { Component } from 'react';
import GoogleMapReact from 'google-map-react';

const AnyReactComponent = ({ text }) => <div>{text}</div>;

class GoogleMap extends Component {
	constructor(props) {
    super(props);
    this.state = {value: '',value1: '',value2: ''};

    this.handleChange = this.handleChange.bind(this);
   }

  handleChange(event) {
    this.setState({value: event.target.value});
    console.log(this.state);
  }

  static defaultProps = {
    center: {
      lat: -30.0277,
      lng: -51.2287
    },
    zoom: 11
  };

  render() {
    return (
      <div style={{ height: '100vh', width: '100%' }}>
        <label>
        	TÃ­tulo:
        	<input type="text" value={this.state.value} onChange={this.handleChange} />
        </label>
        <label>
        	Latitude:
        	<input type="text" value={this.state.value1} onChange={this.handleChange} />
        </label>
        <label>
        	Longitude:
        	<input type="text" value={this.state.value2} onChange={this.handleChange} />
        </label>
        <b>{this.state.value}</b>
        <GoogleMapReact
          bootstrapURLKeys={{ key: 'AIzaSyCw1Cu5QmZqsFLWq-D7m12E3Qqjjj13xWY' }}
          defaultCenter={this.props.center}
          defaultZoom={this.props.zoom}
        >
          <AnyReactComponent
            lat={this.state.value}
            lng={-51.2287}
            text="sim"
          />
        </GoogleMapReact>
      </div>
    );
  }
}

export default GoogleMap;
